layouts.non_amp_template_css:
.c10 {width: 744px;margin-top: 20px;}
.ca{
    display: flex;
    width:100%;
    flex-wrap:wrap;
    margin-bottom: 30px;
}
.c1{
    width:30%;
    margin-right: 25px;
    display: flex;
}
.c1 a{
    line-height:0;
    display:block;
}
.c1 img{
    width:100%;
    height:100%;
}
.c2{
    width:65%;
    font-size: 13px;
    color: #777;
}
.c2 h3{
    font-size: 22px;
    line-height: 1.2;
    font-weight: 500;
    margin: 0 0 6px;
    font-family: roboto,sans-serif;
}
.c2 h3 a{color:#111;text-decoration:none;}

.c10-at-inf{
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 10px;
}
.c10-at-dl{
    font-size: 11px;
    line-height:1.2;
    font-weight: 400;
    flex-grow: 1;
    color: #aaa;
}
.c10-at-inf a{text-decoration:none;}
.c10-at-dl .c10-gp{
    margin: 0 2px;
    color: #ccc;
}
.c10-at-dl .c10-at{font-weight: 600;text-decoration: none;color:#000;text-transform: capitalize;}
.c10-cmn a{
    background: #000;
    padding: 2px 5px 3px 6px;
    position: relative;
    font-size: 11px;
    color: #fff;
    line-height: 1.2;
    text-decoration: none;
}
.c10-cmn a:after{
    position: absolute;
    bottom: -3px;
    left: 0;
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 3px 3px 0 0;
    border-color: #000 transparent transparent transparent;
}
.ca:hover .c2 h1 a{color:#4db2ec;}

@media (max-width: 768px){
    .c10{
        width: 100%;
    }
}
@media(max-width:500px){
    .c2 h1{
        font-size: 14px;
        line-height: 1.2;
        font-weight: 500;
    }
    .c1 {
        margin-right: 15px;
    }
    .c2 p{display:none;}
    .c10-cmn{display:none;}
    .c2 {width: 64%;}

}

