    font-weight: 400;
    flex-grow: 1;
    color:#aaa;
    text-transform: uppercase;
}
.c6-at-dl .c6-gp{
    margin: 0 2px;
    color: #ccc;
}
.c6-at-dl .c6-at{font-weight: 600;text-decoration: none;}
a.c6-cm-n{
    background: #000;
    padding: 2px 5px 3px 6px;
    position: relative;
    font-size: 11px;
    color: #fff;
    line-height: 1.2;
    text-decoration: none;
}
.c6-cm-n:after{
    position: absolute;
    bottom: -3px;
    left: 0;
    content: '';
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 3px 3px 0 0;
    border-color: #000 transparent transparent transparent;
}
.c6-at a{color:#000;text-decoration:none;}
.c6-cntn{
    font-size: 14px;
    line-height: 1.6;
    color:#777;
    margin-top:10px;
}
.c6-cntn p{
    margin:0;
}
.c6-cntn a{text-decoration:none;color:#fff;}
.c6-r-m{
    color: #fff;
    padding: 10px;
    background: #222;
    display: inline-block;
    margin-top: 20px;
    font-size: 12px;
    font-weight: 400;
    line-height: 1;
}
.c6-a:hover .c6-2 h1 a{color:#4db2ec;}
